const supportBtn = document.querySelector('.support-btn');
const supportWindow = document.querySelector('.support-window');
const closeBtn = document.querySelector('.support-window__header-close');

supportBtn.addEventListener('click', function () {
    if (!supportWindow.classList.contains('_show-window')) {
        supportWindow.classList.add('_show-window');
        supportBtn.style.display = 'none';
    }
})

closeBtn.addEventListener('click', function () {
    if (supportWindow.classList.contains('_show-window')) {
        supportWindow.classList.remove('_show-window');
        supportBtn.style.display = 'block';
    }
})